import React, { Component } from 'react';
import './App.css';
import Titles from './components/Titles';
import Forms from './components/Forms';
import Weather from './components/Weather';
import Footer from './components/Footer';
import axios from 'axios';
import yelplogo from './img/yelp.png'; // with import
import main from './img/main.jpg'; // with import

const ApiWeatherKey = process.env.REACT_APP_WEATHER_API_KEY ;
const ApiYelpKey = process.env.REACT_APP_YELP_API_KEY;


class App extends Component {
  state = {
    temp: undefined,
    city: undefined,
    country: undefined,
    humidity: undefined,
    description: undefined,
    park: undefined,
    adress: undefined,
    cities: undefined,
    zip: undefined,
    link: undefined,
    error: undefined,
    check: false
  }

  getYata = (place) => {

      axios.get(`${'https://cors-anywhere.herokuapp.com/'}https://api.yelp.com/v3/businesses/search?location=${place}`, {
        headers: {
          Authorization: `Bearer ${ApiYelpKey}`
      },
        params: {
        categories: 'active',
        title: "Parks",
        alias: "parks",
        term: "park"
      }
      })
      .then((res) => {
        console.log(res);
        let values  = [];
        let images = [];
        let address = [];
        let cities = [];
        let zip = [];
        let links = [];

        for(let i= 0; i< 4; i++ ){
          values.push(res.data.businesses[i].name);
          images.push(res.data.businesses[i].image_url);
          address.push(res.data.businesses[i].location.address1);
          cities.push(res.data.businesses[i].location.city);
          zip.push(res.data.businesses[i].location.zip_code);
          links.push(res.data.businesses[i].url);
        }

        this.setState({
          temp: this.state.temp,
          city: this.state.city,
          country: this.state.country,
          humidity: this.state.humidity,
          description: this.state.description,
          park: values,
          adress: address,
          cities: cities,
          zip: zip,
          link: links,
          img:images,
          error: this.state.error,
          check: true
        });
      })
      .catch((err) => {
      console.log ('error')
      return 'error';
      })
    }




  getData = async (e) => {
    e.preventDefault();


    const city = (e.target.elements.city.value).toLowerCase().split(' ').join('+');
    const country = e.target.elements.country.value;



    const ApiCall = await fetch(`https://api.openweathermap.org/data/2.5/weather?q=${city},${country}&units=imperial&appid=${ApiWeatherKey}`);
    const data = await ApiCall.json();
    console.log(data);


    if(city && country && data.cod !== '404'){

      this.setState({
        temp: data.main.temp,
        city: data.name,
        country: data.sys.country,
        humidity: data.main.humidity,
        description: data.weather[0].description,
        park: 'Loading',
        adress: 'Loading',
        cities: 'Loading',
        zip: 'Loading',
        link: 'Loading',
        img:'Loading',
        error: "",
        check: false
      });
    }else{
      this.setState({
        temp: undefined,
        city: undefined,
        country: undefined,
        humidity: undefined,
        description: undefined,
        park: undefined,
        adress: undefined,
        cities: undefined,
        zip: undefined,
        link: undefined,
        img:'Loading',
        error: data.message,
        check: false
      });

    }
    this.getYata(city);
  }


  render() {    


    const sites = [];
    if(this.state.check){
      for(let i = 0; i <this.state.park.length; i++){
        sites.push(
              <div className="row parks">
                <div className="col-6 col-md park-left">
                  <img src={this.state.img[i]} alt="Img" className={"cardImg"}/>
                </div>
                <div className="col-6 col-md park-right">
                  <h4 className="park--title"><b>{this.state.park[i]}</b></h4>
                  <p className="park-data"><b>Adress:</b> {this.state.adress[i]}</p>
                  <p className="park-data"><b>City:</b> {this.state.cities[i]}</p>
                  <p className="park-data"><b>Zip code:</b> {this.state.zip[i]}</p>
                  <a href={this.state.link[i]}> <img src={yelplogo} alt="gyelp logo" height="50" width="50" className="park-logo"></img> </a>
                </div>
             </div>
             )

      }
    }
    return (
    <div>
          <div className="form-right">
            <div className="">
              <img src={main} alt="runner image" className="park-logo"></img>
            </div>
          <Forms getData={this.getData}/>
                <Weather 
                  temp={this.state.temp}
                  city ={this.state.city}
                  country = {this.state.country}
                  humidity = {this.state.humidity}
                  description = {this.state.description}
                  error = {this.state.error}     
                  />       
          </div>
      {sites}
      <Footer/>
    </div>
    );
  }
}



export default App;
