import React, { Component } from 'react';
import './App.css';
import Jobs from './Jobs/Jobs';

class App extends Component {
  state = {
    jobs: [
      {id:'aa', name: 'Juan', age: 23},
      {id:'ab', name: 'G', age:27 },
      {id:'ac', name: 'Tito', age:18}
    ]
  }



  render() {
    const style = {
      backgroundColor: 'green',
      color: 'white',
      font: 'inherit',
      border: '1px solid blue',
      padding: '8px',
      cursor: 'pointer',
      ':hover': {
        backgroundColor: 'lightgreen',
        color: 'black'
      }
    }


    let Jobs = (
      <div>
        {this.state.jobs.map(job => {
          return <Jobs name={job.name} 
          age={job.age}/>
        })}
      </div>
    );


    return (
      <div className="App">
        <h1> This is react app</h1>
        <p>I am juan</p> 
        <button style={style} onClick={this.togglePersonHandler}> Switch Name</button>
        {Jobs}
      </div>
    );
  }
}

export default App;
