import React, { Component } from 'react';
import './App.css';
import Titles from './components/Titles';
import Forms from './components/Forms';
import Weather from './components/Weather';
import axios from 'axios';

const ApiWeatherKey = process.env.REACT_APP_WEATHER_API_KEY ;
const ApiYelpKey = process.env.REACT_APP_YELP_API_KEY;


class App extends Component {
  state = {
    temp: undefined,
    city: undefined,
    country: undefined,
    humidity: undefined,
    description: undefined,
    park: undefined,
    error: undefined
  }





  getData = async (e) => {
    e.preventDefault();

    const city = e.target.elements.city.value;
    const country = e.target.elements.country.value;





    const ApiCall = await fetch(`https://api.openweathermap.org/data/2.5/weather?q=${city},${country}&units=imperial&appid=${ApiWeatherKey}`);
    const data = await ApiCall.json();
    console.log(data);


    getYata(city);

    let getYata = (place) => {

      axios.get(`${'https://cors-anywhere.herokuapp.com/'}https://api.yelp.com/v3/businesses/search?location=${place}?`, {
        headers: {
          Authorization: `Bearer ${ApiYelpKey}`
      },
        params: {
        categories: 'active',
        title: "Parks",
        alias: "parks",
        term: "park"
      }
      })
      .then((res) => {
      console.log('this is res' + res);
        if(city && country && data.cod !== '404'){

          this.setState({
            temp: data.main.temp,
            city: data.name,
            country: data.sys.country,
            humidity: data.main.humidity,
            description: data.weather[0].description,
            park: res,
            error: ""
          });
        }else{
          this.setState({
            temp: undefined,
            city: undefined,
            country: undefined,
            humidity: undefined,
            description: undefined,
            park: undefined,
            error: data.message
          });

        }
      })
      .catch((err) => {
      console.log ('error')
      return 'error';
      })
    }


  }



  render() {





    return (
    <div>
      <div className="row">
          <div className="col-6 col-md title-left">
            <h5>Left</h5>
            <Titles/>
          </div>
          <div className="col-6 col-md form-right">
            <h5>Right</h5>
                <Forms getData={this.getData}/>
                <Weather 
                  temp={this.state.temp}
                  city ={this.state.city}
                  country = {this.state.country}
                  humidity = {this.state.humidity}
                  description = {this.state.description}
                  park = {this.state.park}
                  error = {this.state.error}     
                  />


          </div>
          <div className="footer">
              <p>Made With</p>
          </div>
      </div>
    </div>
    );
  }
}



export default App;
